import { useContext } from 'react'
import { GlobalStoreContext } from '../store'
import { useHistory } from 'react-router-dom'
import React, { useEffect, useState } from "react";
/*
    This toolbar is a functional React component that
    manages the undo/redo/close buttons.
    
    @author McKilla Gorilla
*/
function EditToolbar() {
    const { store } = useContext(GlobalStoreContext);
    const history = useHistory();
    const [openList, setOpenList] = useState(false);
    const [canUndoActive, setCanUndoActive] = useState(false);
    
    let enabledButtonClass = "top5-button";
    useEffect(() => {
        isCloseDisabled();
        isUndoRedoDisabled();
    }, [store.currentList, store.isItemEditActive]);   

    function isUndoRedoDisabled() {
        console.log("asdjhfkjnaskjdfjkashfijknawjenfjiasdnf");
        if (store.isItemEditActive){
            setCanUndoActive(true);
        }
        else {
            setCanUndoActive(false);
        }
    }

    function isCloseDisabled(){
        if (store.currentList && (store.listMarkedForDeletion==null)){
            setOpenList(true);
        }
        else {
            setOpenList(false);
        }
    }
    function handleUndo() {
        store.undo();
        console.log(store.hasRedo());
    }
    function handleRedo() {
        store.redo();
        console.log(store.hasRedo());
    }
    function handleClose() {
        if (store.currentList && (store.listMarkedForDeletion==null)) {
        history.push("/");
        store.closeCurrentList();
        }
    }
    let editStatus = false;
    if (store.isListNameEditActive) {
        editStatus = true;
    }
    return (
        <div id="edit-toolbar">
            <div
                disabled={editStatus||canUndoActive}
                id='undo-button'
                onClick={handleUndo}
                className={store.hasUndo() ? enabledButtonClass : "top5-button-disabled" }>
                &#x21B6;
            </div>
            <div
                disabled={editStatus || canUndoActive}
                id='redo-button'
                onClick={handleRedo}
                className={store.hasRedo() ? enabledButtonClass : "top5-button-disabled"}>
                &#x21B7;
            </div>
            <div
                disabled={editStatus}
                id='close-button'
                onClick={handleClose}
                className={openList ? enabledButtonClass : "top5-button-disabled"}>
                &#x24E7;
            </div>
        </div>
    )
}

export default EditToolbar;
